mapper.select:
select sm.score, st.studentName, su.subjectName, em.applyEnd, em.applyStart from score_manage sm inner join apply_message am on sm.applyManageId = am.aid inner join student st on am.studentId = st.sid inner join subject su on am.subjectId = su.sid inner join exam_manage em on am.examManageId = em.eid
st.studentName = #{studentName}
or su.subjectName = #{subjectName}
